import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { black } from 'material-ui/styles/colors';
import { Button, Grid, TextField } from '@material-ui/core';
import { SocialIcon } from 'react-social-icons';



const useStyles = makeStyles((theme) => ({
    footer: {
        padding: theme.spacing(3, 2),
        marginTop: 'auto',
        backgroundColor: black,
    },
    root: {
        '& > *': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
    textField: {
        '& label.Mui-focused': {
          color: 'green',
        },
        '& .MuiInput-underline:after': {
          borderBottomColor: 'green',
        },
        '& .MuiOutlinedInput-root': {
          '& fieldset': {
            borderColor: 'red',
          },
          '&:hover fieldset': {
            borderColor: 'yellow',
          },
          '&.Mui-focused fieldset': {
            borderColor: 'green',
          },
        },
      },
    input: {
        color: 'white'

    },
}));

export default function Footer() {
    const classes = useStyles();
    return (
        <footer className={classes.footer}>
            <h2 justify="center">Join Our Newsletter!</h2>
            <form className={classes.form} noValidate autoComplete="off">
                <Grid container
                    direction="row"
                    justify="center"
                    alignItems="center"
                    spacing={2}>
                    <Grid item>
                        <TextField variant="outlined"
                            className={classes.textField}
                            label="First Name"
                            type="text"
                            color="white"
                            required
                            inputProps={{ className: classes.input }}
                        />
                    </Grid>
                    <Grid item>
                        <TextField type="email"
                            label="Email"
                            variant="outlined"
                            className={classes.textField}
                            color="white"
                            required
                            inputProps={{ className: classes.input }}
                        />
                    </Grid>
                    <Grid item>
                        <Button variant="contained">Join</Button>
                    </Grid>
                </Grid>
            </form>
            <br />
            <Grid container
                direction="row"
                justify="center"
                alignItems="center"
                spacing={3}>
                <Grid item>
                    <SocialIcon url="youtube.com" fgColor="#FFFFFF"></SocialIcon>
                </Grid>
                <Grid item>
                    <SocialIcon url="facebook.com" fgColor="#FFFFFF"></SocialIcon>
                </Grid>
                <Grid item>
                    <SocialIcon url="tumblr.com" fgColor="#FFFFFF"></SocialIcon>
                </Grid>
                <Grid item>
                    <SocialIcon url="instagram.com" fgColor="#FFFFFF"></SocialIcon>
                </Grid>
            </Grid>
        </footer>
    );
}
